const galleryItemsModal=document.querySelectorAll(".gallery-item"),modal=document.querySelector(".modal"),modalImage=document.getElementById("modalImage"),closeModalButton=document.querySelector(".close"),prevButton=document.querySelector(".prev"),nextButton=document.querySelector(".next");let currentIndex=0;function openModal(e){modalImage.src=e,modal.style.display="block"}function closeModal(){modal.style.display="none"}function updateModalImage(e){currentIndex=(e+galleryItemsModal.length)%galleryItemsModal.length,modalImage.src=galleryItemsModal[currentIndex].src}function prevImage(){updateModalImage(currentIndex-.5)}function nextImage(){updateModalImage(currentIndex+.5)}galleryItemsModal.forEach(function(e,t){e.addEventListener("click",function(){currentIndex=t,openModal(this.src)})}),closeModalButton.addEventListener("click",closeModal),prevButton.addEventListener("click",prevImage),nextButton.addEventListener("click",nextImage),window.addEventListener("click",function(e){e.target===modal&&closeModal()});